var FormWizard = function () {
    return {
        init: function () {
            if (!jQuery().bootstrapWizard) {
                return
            }
            function format(state) {
                if (!state.id) {
                    return state.text
                }
                return "<img class='flag' src='assets/img/flags/" + state.id.toLowerCase() + ".png'/>&nbsp;&nbsp;" + state.text
            }

            $("#country_list").select2({
                placeholder: "Select",
                allowClear: true,
                formatResult: format,
                formatSelection: format,
                escapeMarkup: function (m) {
                    return m
                }
            });
            var form = $("#submit_form");
            var error = $(".alert-error", form);
            var success = $(".alert-success", form);
            form.validate({
                doNotHideMessage: true,
                errorElement: "span",
                errorClass: "validate-inline",
                focusInvalid: false,
                rules: {
                    username: {minlength: 5, required: true},
                    password: {minlength: 5, required: true},
                    rpassword: {minlength: 5, required: true, equalTo: "#submit_form_password"},
                    fullname: {required: true},
                    email: {required: true, email: true},
                    phone: {required: true},
                    gender: {required: true},
                    address: {required: true},
                    city: {required: true},
                    country: {required: true},
                    card_name: {required: true},
                    card_number: {minlength: 16, maxlength: 16, required: true},
                    card_cvc: {digits: true, required: true, minlength: 3, maxlength: 4},
                    card_expiry_mm: {digits: true, required: true},
                    card_expiry_yyyy: {digits: true, required: true},
                    "payment[]": {required: true, minlength: 1}
                },
                messages: {
                    "payment[]": {
                        required: "Please select at least one option",
                        minlength: jQuery.format("Please select at least one option")
                    }
                },
                errorPlacement: function (error, element) {
                    if (element.attr("name") == "gender") {
                        error.addClass("no-left-padding").insertAfter("#form_gender_error")
                    } else {
                        if (element.attr("name") == "payment[]") {
                            error.addClass("no-left-padding").insertAfter("#form_payment_error")
                        } else {
                            error.insertAfter(element)
                        }
                    }
                },
                invalidHandler: function (event, validator) {
                    success.hide();
                    error.show();
                    App.scrollTo(error, -200)
                },
                highlight: function (element) {
                    $(element).closest(".help-inline").removeClass("ok");
                    $(element).closest(".control-group").removeClass("success").addClass("error")
                },
                unhighlight: function (element) {
                    $(element).closest(".control-group").removeClass("error")
                },
                success: function (label) {
                    if (label.attr("for") == "gender" || label.attr("for") == "payment[]") {
                        label.closest(".control-group").removeClass("error").addClass("success");
                        label.remove()
                    } else {
                        label.addClass("valid ok").closest(".control-group").removeClass("error").addClass("success")
                    }
                },
                submitHandler: function (form) {
                    success.show();
                    error.hide()
                }
            });
            var displayConfirm = function () {
                $(".display-value", form).each(function () {
                    var input = $('[name="' + $(this).attr("data-display") + '"]', form);
                    if (input.is(":text") || input.is("textarea")) {
                        $(this).html(input.val())
                    } else {
                        if (input.is("select")) {
                            $(this).html(input.find("option:selected").text())
                        } else {
                            if (input.is(":radio") && input.is(":checked")) {
                                $(this).html(input.attr("data-title"))
                            } else {
                                if ($(this).attr("data-display") == "card_expiry") {
                                    $(this).html($('[name="card_expiry_mm"]', form).val() + "/" + $('[name="card_expiry_yyyy"]', form).val())
                                } else {
                                    if ($(this).attr("data-display") == "payment") {
                                        var payment = [];
                                        $('[name="payment[]"]').each(function () {
                                            payment.push($(this).attr("data-title"))
                                        });
                                        $(this).html(payment.join("<br>"))
                                    }
                                }
                            }
                        }
                    }
                })
            };
            $("#form_wizard_1").bootstrapWizard({
                "nextSelector": ".button-next",
                "previousSelector": ".button-previous",
                onTabClick: function (tab, navigation, index) {
                    alert("on tab click disabled");
                    return false
                },
                onNext: function (tab, navigation, index) {
                    success.hide();
                    error.hide();
                    if (form.valid() == false) {
                        return false
                    }
                    var total = navigation.find("li").length;
                    var current = index + 1;
                    $(".step-title", $("#form_wizard_1")).text("Step " + (index + 1) + " of " + total);
                    jQuery("li", $("#form_wizard_1")).removeClass("done");
                    var li_list = navigation.find("li");
                    for (var i = 0; i < index; i++) {
                        jQuery(li_list[i]).addClass("done")
                    }
                    if (current == 1) {
                        $("#form_wizard_1").find(".button-previous").hide()
                    } else {
                        $("#form_wizard_1").find(".button-previous").show()
                    }
                    if (current >= total) {
                        $("#form_wizard_1").find(".button-next").hide();
                        $("#form_wizard_1").find(".button-submit").show();
                        displayConfirm()
                    } else {
                        $("#form_wizard_1").find(".button-next").show();
                        $("#form_wizard_1").find(".button-submit").hide()
                    }
                    App.scrollTo($(".page-title"))
                },
                onPrevious: function (tab, navigation, index) {
                    success.hide();
                    error.hide();
                    var total = navigation.find("li").length;
                    var current = index + 1;
                    $(".step-title", $("#form_wizard_1")).text("Step " + (index + 1) + " of " + total);
                    jQuery("li", $("#form_wizard_1")).removeClass("done");
                    var li_list = navigation.find("li");
                    for (var i = 0; i < index; i++) {
                        jQuery(li_list[i]).addClass("done")
                    }
                    if (current == 1) {
                        $("#form_wizard_1").find(".button-previous").hide()
                    } else {
                        $("#form_wizard_1").find(".button-previous").show()
                    }
                    if (current >= total) {
                        $("#form_wizard_1").find(".button-next").hide();
                        $("#form_wizard_1").find(".button-submit").show()
                    } else {
                        $("#form_wizard_1").find(".button-next").show();
                        $("#form_wizard_1").find(".button-submit").hide()
                    }
                    App.scrollTo($(".page-title"))
                },
                onTabShow: function (tab, navigation, index) {
                    var total = navigation.find("li").length;
                    var current = index + 1;
                    var $percent = (current / total) * 100;
                    $("#form_wizard_1").find(".bar").css({width: $percent + "%"})
                }
            });
            $("#form_wizard_1").find(".button-previous").hide();
            $("#form_wizard_1 .button-submit").click(function () {
                alert("Finished! Hope you like it :)")
            }).hide()
        }
    }
}();